mapper.select:
SELECT t5.*, t4.author_name, t4.total, t4.avatar
FROM (
    SELECT t1.*, t2.author_name, t2.avatar
    FROM (
        SELECT count(audio_id) AS total, author_id
        FROM music
        WHERE author_id IN (
            SELECT t3.author_id
            FROM (
                SELECT *
                FROM music_my_singer
                WHERE user_id = #{userId}
                LIMIT #{start}, #{pageSize}
            ) t3
        )
        GROUP BY author_id
    ) t1
        LEFT JOIN music_authors t2 ON t1.author_id = t2.author_id
) t4
    LEFT JOIN music_my_singer t5 ON t4.author_id = t5.author_id

SELECT t4.*, t3.times
FROM (
    SELECT t1.*, t2.times
    FROM (
        SELECT DISTINCT audio_id, id, user_id, create_time, update_time
        FROM music_record
        WHERE user_id = #{userId}
        ORDER BY create_time DESC
        LIMIT #{start}, #{pageSize}
    ) t1
        LEFT JOIN (
            SELECT COUNT(audio_id) AS times, audio_id
            FROM music_record
            WHERE user_id = #{userId}
            GROUP BY audio_id
        ) t2
        ON t1.audio_id = t2.audio_id
) t3
    LEFT JOIN music t4 ON t3.audio_id = t4.audio_id
